using GlattMart.PageModels; using Xamarin.Forms; using Xamarin.Forms.Xaml;  namespace GlattMart.Pages {     [XamlCompilation(XamlCompilationOptions.Compile)]     public partial class PaymentMethodPage : ContentPage     {         private PaymentMethodPageModel PaymentMethodPageModel;         public PaymentMethodPage()         {             InitializeComponent();             PaymentMethodPageModel = new PaymentMethodPageModel();             BindingContext = PaymentMethodPageModel;             NavigationPage.SetBackButtonTitle(this, "");         }          void OnCardNoTextChanged(object sender, TextChangedEventArgs e)         {             var entry = (CustomEntry)sender;             if (entry.Text.Length > 16)             {                 string entryText = entry.Text;                  entryText = entryText.Remove(entryText.Length - 1);                 entry.Text = entryText;             }         }          void OnCvvTextChanged(object sender, TextChangedEventArgs e)         {             var entry = (CustomEntry)sender;             if (entry.Text.Length > 4)             {                 string entryText = entry.Text;                  entryText = entryText.Remove(entryText.Length - 1);                 entry.Text = entryText;             }         }          void OnMMYYTextChanged(object sender, TextChangedEventArgs e)         {             var entry = (CustomEntry)sender;             if (entry.Text.Length > 5)             {                 string entryText = entry.Text;                  entryText = entryText.Remove(entryText.Length - 1);                 entry.Text = entryText;             }         }     } } 